<map version="1.0.1"><node TEXT="欧亚大陆的古典文明" FOLDED="false"><edge COLOR="#BBBBBB"/><node TEXT="最初的欧亚大陆文明" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="古代文明的起源" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="最早的文明中心：苏美尔，位于过去称“美索不达米亚”，现伊拉克共和国南部" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node><node TEXT="特征：城市中心，国家政治权力，纳贡，税收，文字等等" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node><node TEXT="开渠，冶金，犁，风帆，车轮等发现发明进一步推动生活资料进一步丰富，增加了人口。人口的增加反过来又推动了科技的进步。" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node><node TEXT="制度变革开始出现" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="宗教显贵和军事首领、行政首脑统治城市" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="首先是巫师摆脱日常生产，主管宗教活动，管理经济活动" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node><node TEXT="社会成分日益复杂，祭司们的权利转移到世俗新贵手上" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node><node TEXT="战争领导人出现，国王出现，宫廷与寺院出现竞争，后达成伙伴关系" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node></node><node TEXT="中产阶级新市场" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="非农业商品产量大大增加" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="奢侈品" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node><node TEXT="军事装备" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node></node></node><node TEXT="传统议会式微" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node></node></node><node TEXT="古代文明的传播" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="文明一旦生根，狩猎部落群体迅速就被取代" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="农业文明生产效率太高" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="供养阶级社会，农民的生产效率要比部落社会更高" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node><node TEXT="维持现状即可，部落社会缺乏提高生产效率的动力" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node></node></node></node><node TEXT="古代文明的类型" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="两个共性：社会关系的新的不平等和性别关系的新的不平等" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="社会关系从血缘关系转换到纳贡关系" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node><node TEXT="性别不平等" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node></node><node TEXT="美索不达米亚" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="城市类型" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="至公元前3000年，共12个城邦" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node><node TEXT="城邦间连年征战，轻易就被外界的印欧侵入者和闪米特人政府，因此美索不达米亚平原的历史很大程度上是前两者为争夺肥沃的大河流域而进行数千年斗争的历史" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="第一位伟大帝国的奠基者：萨尔贡一世
（约公元前2276-公元前2221年" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="以阿卡德为基地，征服了南抵波斯湾，西达地中海的广大地区" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node></node><node TEXT="汉谟拉比（约公元前1704年-公元前1662年）" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node><node TEXT="赫梯人，亚述人，波斯人，马其顿人，罗马人，阿拉伯人，蒙古人，突厥人" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node></node><node TEXT="货币：银块或银环，每次交易都需称分量" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node><node TEXT="大多数平民是靠当农夫、工匠、商人、渔民和养牛人谋生" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node><node TEXT="土地占有者为国王、祭司和富人" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="将土地划分成小块，连同种子、农具和耕畜分配给农人，农人将剩余产品通过多种形式缴纳" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="记账需要" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="楔形文字" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="语音文字的精髓" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node><node TEXT="书写依然困难，书吏地位高" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node></node></node></node></node><node TEXT="底格里斯河和幼发拉底河每年河水泛滥" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="命运无法把控" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="神灵" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node></node><node TEXT="人与人间充满不安全感" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="《汉谟拉比法典》" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="男女不平等进一步加强" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node></node></node></node></node></node><node TEXT="印度" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="帝国文明" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="自然屏障，外族难以侵犯" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node><node TEXT="在公元前3100年得到统一到公元前525年被波斯人政府，只有三个帝国通知过埃及，且过渡间隔短" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node><node TEXT="尼罗河温和" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="人民乐观" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node></node><node TEXT="王权通过以宰相为首的官僚机构得到加强" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="宰相" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node><node TEXT="掌玺大臣（掌管尼罗河的交通运输）" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node><node TEXT="赏赐大臣（负责所有牲畜）" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node><node TEXT="财政大臣（掌管全国的财政分署和仓库，负责征收税赋；年景不好时，还要负责分配种子和牲畜）" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node><node TEXT="州长" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node><node TEXT="市长" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node></node><node TEXT="国家对经济生活绝对控制，负责产品的分配" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node><node TEXT="奢侈品制造技艺举世公认" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node></node></node><node TEXT="克里特" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="米诺斯文明（海洋文明）" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="发展核心是贸易" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node><node TEXT="海上力量非常强大，岛上城市几乎不设防" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="村庄形态是由大量村庄组成，而不是大陆上的宏大神庙和皇宫加上周边的贫民区" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node></node><node TEXT="文化以自然描绘为主" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node><node TEXT="远离战争，男女平等" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node></node></node><node TEXT="印度河" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="保守的，浓厚的宗教色彩和高度的计划性" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="建筑一致，度量衡一致" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node></node><node TEXT="农业文明，有一定贸易" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node><node TEXT="文字未能解读，起因和详情尚不清楚" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node></node><node TEXT="商朝" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="黄河流域北部" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node><node TEXT="尽管有入侵者，但很快就被同化，因此中国文明不间断地延续下来" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node><node TEXT="甲骨文" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node></node></node><node TEXT="游牧民族获得权力" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="古代文明崩溃" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="内因：铜和青铜昂贵且稀少，被君主、政治、军事集团控制，无法使大部分人得到武装" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node><node TEXT="外因：马的驯养和冶铁技术发展，使得游牧民武装增强" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node><node TEXT="游牧集团：" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="南部沙漠地带的闪米特部落民" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="占据了从地中海到底格里斯河、从托罗斯山脉到亚丁之间的地区" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node></node><node TEXT="欧亚大草原西部的印欧人" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="威胁地区：中东、巴尔干半岛和印度河流域" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node></node><node TEXT="大草原东部的蒙古-突厥各族" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node></node><node TEXT="趋势：游牧集团从东向西推进（西部环境更好，资源更多）" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node></node></node><node TEXT="游牧民族摧毁古代文明" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="综述" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="时间" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="第一次：公元前1700年至公元前1500年之间，入侵者通常手持青铜武器，驾着马拉的战车侵入文明中心；" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node><node TEXT="第二次：在公元前1200至公元前1100年之间，入侵者通常是骑在马上，用铁制武器作战" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node></node></node><node TEXT="中东" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="首次入侵:公元前2000年前后，印欧语系的赫梯人进入小亚细亚" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="建立包括小亚细亚和叙利亚大部分、不包括美索不达米亚的庞大帝国" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node></node><node TEXT="随后是印欧语系的喀西特人和胡利安人" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node><node TEXT="闪米特人（喜客索人）入侵埃及" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node><node TEXT="第二次入侵：公元前1200年左右" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="闪米特人入侵" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="腓尼基人占领地中海沿海一带" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node><node TEXT="阿拉米人占据了叙利亚、巴基斯坦和美索不达米亚北部" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node><node TEXT="希伯来人占领了巴基斯坦和叙利亚" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node></node><node TEXT="公元前1100年前后，第二亚述帝国凭借铁制武器、训练有素的军队、有效官僚机构进行扩张，建立了以尼尼微为首都，囊括整个美索不达米亚、小亚细亚、叙利亚、巴勒斯坦和埃及的第二亚述帝国。" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="公元前612年被敌对者联盟摧毁" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node></node><node TEXT="波斯帝国" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="国王居鲁士" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="统治了西面尼罗河到东面印度河的所有地区，中东第一次处于一个国家的统治之下" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node></node></node></node></node><node TEXT="希腊" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="最早入侵的是印欧语系的亚该亚人，在色萨利到伯罗奔尼撒半岛南端的广大区域建立了许多小王国" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node><node TEXT="伯罗奔尼撒半岛离克里特岛最近，所以最先进。迈锡尼是伯罗奔尼撒半岛的中心。" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><node TEXT="迈锡尼和克里特岛人一样建立海上力量，后劫掠克里特岛，使得米诺斯文明灭绝。" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node></node><node TEXT="新入侵的多利安人攻略迈锡尼，希腊行政制度瓦解直至公元前800年诸城邦崛起。" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/></node><node TEXT="希腊人的宝贵遗产，四大史诗（《伊利亚特》《奥德赛》《工作与时日》《神谱》" FOLDED="false" POSITION="right"><edge COLOR="#DC306C"/><